mapper.update:
UPDATE chat_room
SET is_deleted = TRUE
WHERE id = #{id}

mapper.select:
SELECT id, user_id, recent_msg, created_at, is_deleted
FROM chat_room
WHERE id = #{id}
  AND user_id = #{userId}
  AND is_deleted = FALSE

mapper.update:
UPDATE chat_room
SET recent_msg = #{recentMsg}
WHERE id = #{id}
